import React, { Component } from "react";
import Nav from "Components/Nav";
import Tables from "Components/Tables";
import "./Cell.scss";
import config from "config.js";

class Cell extends Component {
  constructor() {
    super();
    this.state = {
      cells: [],
      modalOn: false,
      inputVal: "",
      selectedCell: ""
    };

    this.page = React.createRef();
  }

  componentDidMount() {
    this.fetchCells();
  }

  fetchCells = async () => {
    const response = await fetch(`${config.url}cell`).then(res => res.json());

    const cells = response.map(res => {
      return res.name;
    });

    this.setState({ cells });
  };

  handleClick = cell => {
    this.setState({ modalOn: true, inputVal: cell });
  };

  handleChange = e => {
    const val = e.target.value;

    this.setState({ inputVal: val });
  };

  cancelInput = e => {
    e.target.contains(this.page.current) &&
      this.setState({ modalOn: false, inputVal: "" });
  };

  render() {
    const { cells, inputVal, modalOn } = this.state;

    return (
      <>
        <Nav />
        <div className="page" ref={this.page} onClick={this.cancelInput}>
          <main>
            <div className="title">
              <span>셀 관리</span>
            </div>
            <div className="tools">
              <button onClick={this.handleClickTool}>셀 추가</button>
            </div>
            <div className="tables">
              <Tables onClick={this.handleClick} listData={cells} />
            </div>
          </main>
          {modalOn && (
            <div className="smallInput">
              <div className="title">셀 이름을 변경하세요</div>
              <input onChange={this.handleChange} value={inputVal} />
              <div className="tools">
                <button onClick={this.handleConfirm}>확인</button>
                <button onClick={this.cancelInput}>취소</button>
              </div>
            </div>
          )}
        </div>
      </>
    );
  }
}

export default Cell;
