import React, { Component } from "react";
import "./EmployeeTable.scss";
import Nav from "Components/Nav";
import SideBar from "./SideBar";
import Tables from "./Tables";

class EmployeeTable extends Component {
  state = {
    members: [],
    memberInfo: {}
  };

  componentDidMount() {
    this.fetchMembers();
  }

  fetchMembers = async () => {
    const { members } = this.state;

    const list = await fetch("http://localhost:3030/member").then(res =>
      res.json()
    );

    this.setState({
      members: members.concat(list)
    });
  };

  handleClick = async name => {
    const data = await fetch(`http://localhost:3030/member/${name}`).then(res =>
      res.json()
    );

    this.setState({ memberInfo: data.member });
  };

  render() {
    const { members, memberInfo } = this.state;

    return (
      <>
        <div className="modal">
          <div className="background">
            <div className="card">
              <span className="cancel"></span>
              <div className="row">
                <div className="key">닉네임</div>
                <input value={"닉네임"} className="value" />
              </div>
              <div className="row">
                <div className="key">소속</div>
                <input value={"닉네임"} className="value" />
              </div>
              <div className="row">
                <div className="key">입사일</div>
                <input value={"닉네임"} className="value" />
              </div>
              <div className="button-wrapper">
                <button className="change">수정하기</button>
                <button className="delete">삭제하기</button>
              </div>
            </div>
          </div>
        </div>

        <div className="page">
          <SideBar />
          <main>
            <div className="title">
              <span>클둥이 목록</span>
            </div>
            <div className="tables">
              <Tables onClick={this.handleClick} memberList={members} />
            </div>
          </main>
          {/* <Nav /> */}
        </div>
      </>
    );
  }
}

export default EmployeeTable;
