import React, { Component } from "react";

class Modal extends Component {
  constructor(props) {
    super();
    const { _id, cell, enrolledIn, nickName } = props.memberInfo;

    this.state = {
      nickName,
      cell,
      enrolledIn,
      _id,
      isWrongCell: false
    };
  }

  handleClickConfirm = async () => {
    const { _id, nickName, cell, enrolledIn } = this.state;
    const { cancelModal } = this.props;

    const data = JSON.stringify({ nickName, cell, enrolledIn });

    const response = await fetch(`http://localhost:3030/member/${_id}`, {
      method: "POST",
      body: data
    }).then(res => res.json());

    console.log(response.updated);
    response.message === "success" && cancelModal();
  };

  handleClickDelete = async () => {
    const { _id } = this.state;

    const response = await fetch(`http://localhost:3030/member/${_id}`, {
      method: "delete"
    }).then(res => res.json());

    console.log(response);
  };

  handleClickCancel = () => {
    const { cancelModal } = this.props;

    cancelModal();
  };

  handleChange = e => {
    const key = e.target.name;
    const value = e.target.value;

    this.setState({ [key]: value });
  };

  render() {
    const { nickName, enrolledIn, cell, isWrongCell } = this.state;

    return (
      <div className="modal">
        <div className="background">
          <div className="card">
            <span onClick={this.handleClickCancel} className="cancel"></span>
            <div className="row">
              <div className="key">닉네임</div>
              <input
                name="nickName"
                onChange={this.handleChange}
                value={nickName}
                className="value"
              />
            </div>
            <div className="row">
              <div className="key">소속</div>
              <input
                name="cell"
                onChange={this.handleChange}
                value={cell}
                className={isWrongCell ? "wrongCell" : "value"}
              />
            </div>
            <div className="row">
              <div className="key">입사일</div>
              <input
                name="enrolledIn"
                onChange={this.handleChange}
                value={enrolledIn}
                className="value"
              />
            </div>
            <div className="button-wrapper">
              <button onClick={this.handleClickConfirm} className="change">
                확인
              </button>
              <button onClick={this.handleClickDelete} className="delete">
                삭제
              </button>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default Modal;
