import React from "react";

export default function ModalTemplate(contents) {
  const handleClickBg = e => {
    const { cancelModal } = this.props;
    e.target.contains(this.card.current) && cancelModal();
  };

  return (
    <div className="modal">
      <div onClick={this.handleClickBg} className="background" ref={this.card}>
        <div className="card">
          <span onClick={this.handleClickCancel} className="cancel"></span>
          {contents}
        </div>
      </div>
    </div>
  );
}
