import React, { Component } from "react";

class Tools extends Component {
  componentDidMount() {
    window.addEventListener("keypress", this.handleKeypress);
  }

  componentWillUnmount() {
    window.removeEventListener("keypress", this.handleKeypress);
  }

  handleKeypress = e => {
    e.keyCode === 13 && this.handleClickConfirm();
  };

  handleClickBg = e => {
    const { cancelModal } = this.props;
    e.target.contains(this.card.current) && cancelModal();
  };

  handleClickCancel = () => {
    const { cancelModal } = this.props;

    cancelModal();
  };

  render() {
    return (
      <div className="modal">
        <div
          onClick={this.handleClickBg}
          className="background"
          ref={this.card}
        >
          <div className="cells">
            {cells.map(cell => {
              const { name } = cell;

              return <div className="cell">{name}</div>;
            })}
          </div>
          <div className="card">
            <span onClick={this.handleClickCancel} className="cancel" />

            <div className="button-wrapper">
              <button onClick={this.handleClickConfirm} className="change">
                확인
              </button>
              <button onClick={this.handleClickDelete} className="delete">
                삭제
              </button>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default Tools;
