import React, { Component } from "react";
import "./Employeetable.scss";
import Nav from "Components/Nav";
import Tables from "./Tables";
import Modal from "./Modal";
import Tools from "./Tools";
import config from "config.js";
import { create } from "domain";

class EmployeeTable extends Component {
  state = {
    members: [],
    memberInfo: {},
    cells: [],
    isModalOn: false,
    createMember: false,
    inputVal: ""
  };

  componentDidMount() {
    this.fetchMembers();
  }

  fetchMembers = async () => {
    const { members } = this.state;

    const list = await fetch(`${config.url}member`).then(res => res.json());

    this.setState({
      members: members.concat(list)
    });
  };

  handleClick = async name => {
    const memberData = await fetch(`${config.url}member/${name}`).then(res =>
      res.json()
    );

    const cellData = await fetch(`${config.url}cell`).then(res => res.json());

    this.setState({
      memberInfo: memberData.member,
      cells: cellData,
      isModalOn: true
    });
  };

  cancelModal = () => {
    this.setState({ isModalOn: false, createMember: false });
  };

  createMember = () => {
    this.setState({ createMember: true });
  };

  handleChange = e => {
    const inputVal = e.target.value;

    this.setState({ inputVal });
  };

  handleConfirm = async () => {
    const { inputVal } = this.state;
    const url = config.url + "member";
    const data = JSON.stringify({
      name: inputVal
    });

    const response = await fetch(url, {
      method: "POST",
      body: data
    }).then(res => res.json());

    console.log(response);
  };

  render() {
    const {
      members,
      memberInfo,
      isModalOn,
      cells,
      createMember,
      inputVal
    } = this.state;

    return (
      <>
        {isModalOn && (
          <Modal
            cells={cells}
            cancelModal={this.cancelModal}
            memberInfo={memberInfo}
          />
        )}
        {createMember && (
          <Tools
            onChange={this.handleChange}
            inputVal={inputVal}
            cancelModal={this.cancelModal}
            handleConfirm={this.handleConfirm}
          />
        )}
        <Nav />
        <div className="page">
          <main>
            <div className="title">
              <span>클둥이 목록</span>
            </div>
            <div className="tools">
              <button onClick={this.createMember}>뉴비 추가</button>
            </div>
            <div className="tables">
              <Tables onClick={this.handleClick} listData={members} />
            </div>
          </main>
        </div>
      </>
    );
  }
}

export default EmployeeTable;
