import React, { Component } from "react";
import "./EmployeeTable.scss";
import Nav from "Components/Nav";
import SideBar from "./SideBar";
import Tables from "./Tables";

class EmployeeTable extends Component {
  state = {
    members: [],
    memberInfo: {}
  };

  componentDidMount() {
    this.fetchMembers();
  }

  fetchMembers = async () => {
    const { members } = this.state;

    const list = await fetch("http://localhost:3030/member").then(res =>
      res.json()
    );

    this.setState({
      members: members.concat(list)
    });
  };

  handleClick = async name => {
    const response = await fetch(
      `http://localhost:3030/member/${name}`
    ).then(res => res.json());
    this.setState({ memberInfo: response.member });
  };

  render() {
    const { members, memberInfo } = this.state;
    console.log(memberInfo);

    return (
      <div className="page">
        <SideBar />
        <main>
          <div className="title">
            <span>클둥이 목록</span>
          </div>
          <div className="tables">
            <Tables onClick={this.handleClick} memberList={members} />
          </div>
        </main>
        {/* <Nav /> */}
      </div>
    );
  }
}

export default EmployeeTable;
