import React, { Component } from "react";

class Tools extends Component {
  constructor() {
    super();
    this.card = React.createRef();
  }
  componentDidMount() {
    window.addEventListener("keypress", this.handleKeypress);
  }

  componentWillUnmount() {
    window.removeEventListener("keypress", this.handleKeypress);
  }

  handleKeypress = e => {
    e.keyCode === 13 && this.handleClickConfirm();
  };

  handleClickBg = e => {
    const { cancelModal } = this.props;

    e.target.contains(this.card.current) && cancelModal();
  };

  handleClickCancel = () => {
    const { cancelModal } = this.props;

    cancelModal();
  };

  handleChange = e => {
    const { onChange } = this.props;

    onChange(e);
  };

  handleConfirm = () => {
    const { handleConfirm } = this.props;

    handleConfirm();
  };

  render() {
    const { inputVal } = this.props;

    return (
      <div className="modal">
        <div
          onClick={this.handleClickBg}
          className="background"
          ref={this.card}
        >
          <div className="card">
            <span onClick={this.handleClickCancel} className="cancel" />
            <div className="input-wrapper">
              <div className="title">추가할 사람</div>
              <input
                onChange={this.handleChange}
                value={inputVal}
                placeholder="닉네임을 입력해주세요"
              />
            </div>
            <div className="button-wrapper">
              <button onClick={this.handleConfirm} className="change">
                확인
              </button>
              <button onClick={this.handleDelete} className="delete">
                삭제
              </button>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default Tools;
