import React, { Component } from "react";
import { withRouter } from "react-router-dom";
import NavTemplate from "./NavTemplate";
import Icon from "./Icon";
import "./Nav.scss";

export class Nav extends Component {
  constructor(props) {
    super(props);
    this.state = {
      isRoulette: false,
      isEmployeetable: false,
      isAdminPage: false,
      isCellPage: false
    };
  }
  componentDidMount() {
    this.setState({
      isRoulette: this.props.match.path === "/roulette" && true,
      isEmployeetable: this.props.match.path === "/employees" && true,
      isAdminPage: this.props.match.path === "/admin" && true
    });
  }

  handleRoulette = () => {
    this.setState({
      isRoulette: true,
      isCellPage: false,
      isEmployeetable: false,
      isAdminPage: false
    });

    this.props.history.push("/roulette");
  };

  handleEmployeetable = () => {
    this.setState({
      isEmployeetable: true,
      isAdminPage: false,
      isCellPage: false,
      isRoulette: false
    });

    this.props.history.push("/employees");
  };

  handleAdminPage = () => {
    this.setState({
      isAdminPage: true,
      isCellPage: false,
      isEmployeetable: false,
      isRoulette: false
    });

    this.props.history.push("/admin");
  };

  handleCell = () => {
    this.setState({
      isCellPage: true,
      isEmployeetable: false,
      isAdminPage: false,
      isRoulette: false
    });

    this.props.history.push("/cell");
  };

  render() {
    const { isRoulette, isEmployeetable, isAdminPage, isCellPage } = this.state;

    return (
      <div className="navigation-bigger-wrapper">
        <div className="navigation-wrapper">
          <Icon />
          <div className="navigation">
            <NavTemplate
              text="점술판"
              onClick={this.handleRoulette}
              isActive={isRoulette}
            />
            <NavTemplate
              text="클둥이 목록"
              onClick={this.handleEmployeetable}
              isActive={isEmployeetable}
            />
            <NavTemplate
              text="관리자"
              onClick={this.handleAdminPage}
              isActive={isAdminPage}
            />
            <NavTemplate
              text="셀 관리"
              onClick={this.handleCell}
              isActive={isCellPage}
            />
          </div>
        </div>
      </div>
    );
  }
}

export default withRouter(Nav);
