import React, { Component } from "react";
import "./Employeetable.scss";
import Nav from "Components/Nav";
import Tables from "./Tables";
import Modal from "./Modal";
import Tools from "./Tools";
import config from "config.js";

class EmployeeTable extends Component {
  state = {
    members: [],
    memberInfo: {},
    cells: [],
    isModalOn: false,
    useTool: true
  };

  componentDidMount() {
    this.fetchMembers();
  }

  fetchMembers = async () => {
    const { members } = this.state;

    const list = await fetch(`${config.url}member`).then(res => res.json());

    this.setState({
      members: members.concat(list)
    });
  };

  handleClick = async name => {
    const memberData = await fetch(
      `http://localhost:3030/member/${name}`
    ).then(res => res.json());

    const cellData = await fetch(`http://localhost:3030/cell`).then(res =>
      res.json()
    );

    this.setState({
      memberInfo: memberData.member,
      cells: cellData,
      isModalOn: true
    });
  };

  cancelModal = () => {
    this.setState({ isModalOn: false });
  };

  handleClickTool = () => {
    const { useTool } = this.state;

    this.setState({ useTool: !useTool });
  };

  render() {
    const { members, memberInfo, isModalOn, cells, useTool } = this.state;

    return (
      <>
        {isModalOn && (
          <Modal
            cells={cells}
            cancelModal={this.cancelModal}
            memberInfo={memberInfo}
          />
        )}
        {useTool && <Tools cancelModal={this.handleClickTool} />}
        <Nav />
        <div className="page">
          <main>
            <div className="title">
              <span>클둥이 목록</span>
            </div>
            <div className="tools">
              <button onClick={this.handleClickTool}>뉴비 추가</button>
            </div>
            <div className="tables">
              <Tables onClick={this.handleClick} listData={members} />
            </div>
          </main>
        </div>
      </>
    );
  }
}

export default EmployeeTable;
