import React, { Component } from "react";

class Tools extends Component {
  constructor() {
    super();
    this.card = React.createRef();
  }
  componentDidMount() {
    window.addEventListener("keypress", this.handleKeypress);
  }

  componentWillUnmount() {
    window.removeEventListener("keypress", this.handleKeypress);
  }

  handleKeypress = e => {
    e.keyCode === 13 && this.handleClickConfirm();
  };

  handleClickBg = e => {
    const { cancelModal } = this.props;

    e.target.contains(this.card.current) && cancelModal();
  };

  handleClickCancel = () => {
    const { cancelModal } = this.props;

    cancelModal();
  };

  handleChange = e => {
    const { onChange } = this.props;

    onChange(e);
  };

  handleConfirm = () => {
    const { handleConfirm } = this.props;

    handleConfirm();
  };

  handleSelect = e => {
    console.log(e.target);
  };

  render() {
    const { name, cell, cells, enrolledIn } = this.props;

    return (
      <div className="modal">
        <div
          onClick={this.handleClickBg}
          className="background"
          ref={this.card}
        >
          <div className="card">
            <span onClick={this.handleClickCancel} className="cancel" />
            <div className="input-wrapper">
              <input
                onChange={this.handleChange}
                value={name}
                placeholder="닉네임"
              />
              <select>
                {cells.map(cell => {
                  return (
                    <option onClick={this.handleSelect} value={cell._id}>
                      {cell.name}
                    </option>
                  );
                })}
              </select>
              <input
                onChange={this.handleChange}
                value={enrolledIn}
                placeholder="입사일 1999.01.01"
              />
            </div>
            <div className="button-wrapper">
              <button onClick={this.handleConfirm} className="change">
                확인
              </button>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default Tools;
