import React, { Component } from "react";

class Modal extends Component {
  constructor(props) {
    super();
    const { _id, cell, enrolledIn, nickName } = props.memberInfo;

    this.state = {
      nickName,
      cell,
      enrolledIn,
      _id
    };
  }

  handleClickConfirm = () => {
    const { id } = this.state;

    fetch(`http://localhost:3030/member/${id}`, {
      method: "POST",
      body: {}
    });
  };

  handleClickDelete = async () => {
    const id = this.state.memberInfo._id;
    const result = await fetch(`http://localhost:3030/member/${id}`, {
      method: "DELETE"
    }).then(res => res.json());

    console.log(result);
  };

  handleClickCancel = () => {
    const { cancelModal } = this.props;

    cancelModal();
  };

  render() {
    const { nickName, enrolledIn, cell } = this.state.memberInfo;

    return (
      <div className="modal">
        <div className="background">
          <div className="card">
            <span onClick={this.handleClickCancel} className="cancel"></span>
            <div className="row">
              <div className="key">닉네임</div>
              <input value={nickName} className="value" />
            </div>
            <div className="row">
              <div className="key">소속</div>
              <input value={cell} className="value" />
            </div>
            <div className="row">
              <div className="key">입사일</div>
              <input value={enrolledIn} className="value" />
            </div>
            <div className="button-wrapper">
              <button onClick={this.handleClickConfirm} className="change">
                확인
              </button>
              <button onClick={this.handleClickDelete} className="delete">
                삭제
              </button>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default Modal;
